extension Binding {
    func onChange(_ handler: @escaping (Value) -&gt; Void) -&gt; Binding&lt;Value&gt; {
        Binding(
            get: { self.wrappedValue },
            set: { newValue in
                self.wrappedValue = newValue
                handler(newValue)
            }
        )
    }
}

